import React from 'react';
import TheChart from './TheChart';

function ChartContainer({ tableData }) {
  const newCandidateData = tableData.map((candidate) => {
    let addedDate = new Date(candidate.since);
    let today = new Date();
    let days = Math.floor((today - addedDate) / (1000 * 60 * 60 * 24));
    return days;
  });

  console.log('newCandidateData', newCandidateData);

  const data = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'First dataset',
        // data: [33, 53, 85, 41, 44, 65],
        data: newCandidateData,
        fill: true,
        backgroundColor: 'rgba(75,192,192,0.2)',
        borderColor: 'rgba(75,192,192,1)',
      },
    ],
  };

  return (
    <div>
      <TheChart chartData={data} />
    </div>
  );
}

export default ChartContainer;
