import React from 'react';
import TheChart from './TheChart';

function ChartContainer({ tableData }) {
  const allCandidates = tableData.map((candidate) => {
    let addedDate = new Date(candidate.since);
    // dayAdded: day of the month candidate was added
    let dayAdded = addedDate.getDate(addedDate);
    let today = new Date();
    let days = Math.floor((today - addedDate) / (1000 * 60 * 60 * 24));

    // console.log('addedDate', addedDate);
    // console.log('dayAdded', dayAdded);
    // console.log('today', today);
    // console.log('days', days);
    return days;
  });
  const newCandidates = allCandidates.filter((candidate) => candidate <= 5);

  let curr = new Date();
  let day = curr.getDay();
  let firstday = new Date(curr.getTime() - 60 * 60 * 24 * day * 1000); //will return firstday (ie sunday) of the week
  let lastday = new Date(firstday.getTime() + 60 * 60 * 24 * 6 * 1000); //adding (60*60*6*24*1000) means adding six days to the firstday which results in lastday (saturday) of the week

  console.log('cur', curr);
  console.log('day', day);
  console.log('firstday', firstday);
  console.log('lastday', lastday);

  // console.log('allCandidates', allCandidates);
  // console.log('newCandidates', newCandidates);

  const data = {
    labels: ['Wk 1', 'Wk 2', 'Wk 3', 'Wk 4'],
    datasets: [
      {
        label: '',
        // data: [33, 53, 85, 41, 44, 65],
        data: newCandidates,
        fill: true,
        backgroundColor: 'rgba(0,0,0,0.2)',
        borderColor: 'rgba(0,0,0,1)',
      },
    ],
  };

  return (
    <div>
      <TheChart chartData={data} />
    </div>
  );
}

export default ChartContainer;
